mapper.select:
select * 
from code_info 


		and group_code_id = #{group_code_id}


		and code_id = #{code_id}


ORDER BY SORT_ORDR

select count(*) from document
and DOCUMENT_WRITER_NO = #{empNo}
and document_no in ( select document_no from approval where emp_no = #{empNo} and approval_status = '대기') and document_status like '%대기%'

mapper.update:
UPDATE APPROVAL SET APPROVAL_STATUS = '승인', approval_opinion=#{apprOpinion,jdbcType=VARCHAR}, APPROVAL_ENDDATE = SYSDATE WHERE DOCUMENT_NO = #{documentNo,jdbcType=BIGINT} AND EMP_NO = #{empNo,jdbcType=BIGINT}
SELECT CASE WHEN COUNT(*) = SUM(CASE WHEN APPROVAL_STATUS ='승인' THEN 1 ELSE 0 END) THEN 'Y' ELSE 'N' END CHK FROM APPROVAL
DOCUMENT_NO = #{documentNo,jdbcType=BIGINT} AND APPROVAL_TYPE in (select approval_type from approval where document_no = #{documentNo,jdbcType=BIGINT} and EMP_NO = #{empNo,jdbcType=BIGINT})

  		UPDATE APPROVAL 
  		SET APPROVAL_STATUS = '반려', approval_opinion=#{apprOpinion,jdbcType=VARCHAR}, APPROVAL_ENDDATE = SYSDATE
		WHERE DOCUMENT_NO = #{documentNo,jdbcType=BIGINT} AND EMP_NO = #{empNo,jdbcType=BIGINT}
